const float PI = acos(-1.);

mat2 rotate2D(in float a) {
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col = vec3(0);

    float T = mod(iTime * 2., 10.);

    //int s = int[](182, 150, 78, 47, 104, 85, 16, 212, 15, 142)[int(T)];

    int s;
    if(T < 1.) {
        s = 182;
    } else if(T < 2.) {
        s = 150;
    } else if(T < 3.) {
        s = 78;
    } else if(T < 4.) {
        s = 47;
    } else if(T < 5.) {
        s = 104;
    } else if(T < 6.) {
        s = 85;
    } else if(T < 7.) {
        s = 16;
    } else if(T < 8.) {
        s = 212;
    } else if(T < 9.) {
        s = 15;
    } else {
        s = 142;
    }

    float scale = 0.3;
    float n = fract(sin(dot(ceil(p * rotate2D(PI / 4.) / sqrt(2.) / scale + 0.5 + float(s)), vec2(3, 5))) * 50.);

    p.y = abs(p.y);
    p.y -= scale;
    p = abs(p);
    p = p.y > p.x ? p.yx : p;
    p.x -= scale;
    p.x = abs(p.x);

    float d = max(p.x + p.y - scale * 0.667, p.x);

    if(d < scale * 0.267 && n < 0.5) {
        col.g += 1.;
    }

    fragColor = vec4(col, 1.);
}
