void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uvc = uv * 2.0 - 1.0;
    float r = sqrt(dot(uvc, uvc) / 2.0);
    float theta = atan(uvc.y, uvc.x);

    float distorted_r = r + (0.5 + pow(sin(iTime * 0.0333333), 2.0) * 0.5);
    float distortion_theta = pow(sin(iTime * 0.117647), 2.0) * 10.0;
    float twist_theta = theta + cos(distorted_r * (1.0 + pow(sin(iTime * 0.142857), 2.0) * distortion_theta) * 3.0);

    float rs = pow(sin(iTime * 0.1111111) * 4.5 + 2.83333, 2.0);
    float gs = pow(sin(iTime * 0.0909091) * 4.5 + 2.83333, 2.0);
    float bs = pow(sin(iTime * 0.0769231) * 4.5 + 2.83333, 2.0);

    float ripple = pow(sin(twist_theta * 4.0), rs);
    float ripple2 = pow(sin(twist_theta * 4.0 + 3.141592654 * 0.125), gs);
    float ripple3 = pow(sin(twist_theta * 4.0 + 3.141592654 * 0.375), bs);

    fragColor = vec4(distorted_r * ripple, distorted_r * ripple2, distorted_r * ripple3, 1.0);
}
