mat2 rotate(float a)
{
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}

float random(float t)
{
    return fract(sin(t)*3738.392);
}

float noise(float t)
{
    float i = floor(t);
    float f = fract(t);
    return mix(random(i), random(i+1.),f);
}

float offset(float t, float a)
{
    return noise(t*2.-a*30.)*(sin(t*0.4)*0.3+0.35)+1.;
}

float spiral(vec2 uv, float t)
{
    float r = distance(vec2(0.),uv);//*(noise(t)+1.);
    float a = atan(uv.y,uv.x);

    float rx = r * offset(t, a);

    float v, p = 0.;//a*0.03+noise(a*20.+t*3.)*0.02;
    //float p = 0.;

    for(int i = 0; i < 9; i++)
    {
        v = a*0.03+noise(a*20.+t*3.)*0.02;
        p = max(step(v, rx)-step(v+0.01, rx), p);
        a += 3.14;
        rx = r * offset(t, a);
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5);
    uv *= rotate(iTime*0.2);

    float pct = spiral(uv, iTime*3.);

    // Time varying pixel color
    vec3 col = pct*(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)));
    col = vec3(pct);

    // Output to screen
    fragColor = vec4(col,1.0);
}
