void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale1 = mix(3.,19.,smoothstep(-.7,.7,sin(iTime/3.)));
    float scale2 = mix(1.,4.,smoothstep(-.7,.7,sin(iTime/(3.*1.68))));
    float imix = .7;


    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float a = iTime*.2;
    uv = uv*cos(a) + uv.yx*vec2(1,-1)*sin(a);

    vec2 uv2 = abs(fract(uv*scale1)-.5)*2.;

    uv = uv*cos(-2.*a) + uv.yx*vec2(1,-1)*sin(-2.*a);

    uv = mix( uv2, abs(fract(uv*scale2)-.5)*2., imix);

    uv = uv*cos(a) + uv.yx*vec2(1,-1)*sin(a);

    uv *= vec2(.07,.5);
    uv -= iTime*vec2(.1,.13);

    // Time varying pixel color
//    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = texture(iChannel0,uv+iTime*vec2(.1,.13)).rgb;

    //col = 1.-col;
    col = col.rbg;
    col = clamp(col*8.-7.*dot(col,vec3(.2,.75,.05)),.0,1.); // saturation boost

    // Output to screen
    fragColor = vec4(col,1.0);
}
