const float PHI = 1.61803398874989484820459; // Î¦ = Golden Ratio 

float gold_noise(in vec2 xy, in float seed)
{
    return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}

vec3 noise (vec2 uv) {
    uv = uv * 1000. + vec2(-1.,0.);
    float seed = iTime;
    return vec3(gold_noise(uv, fract(seed)+1.0), gold_noise(uv, fract(seed)+2.0), gold_noise(uv, fract(seed)+3.0));    
}

vec3 lerp(vec3 a, vec3 b, float x) {
    return (1. - x) * a + x * b;
}

vec3 blerp(vec3 tl, vec3 tr, vec3 bl, vec3 br, vec2 uv) {
    return lerp(lerp(tl, tr, uv.x), lerp(bl, br, uv.x), uv.y);
}

vec3 cerp(vec3 p0, vec3 p1, vec3 p2, vec3 p3, float x) {
    return p1 + 0.5 * x*(p2 - p0 + x*(2.0*p0 - 5.0*p1 + 4.0*p2 - p3 + x*(3.0*(p1 - p2) + p3 - p0)));
}

vec3 bcerp(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4, vec3 p5, vec3 p6, vec3 p7, vec3 p8, vec3 p9, vec3 p10, vec3 p11, vec3 p12, vec3 p13, vec3 p14, vec3 p15, vec2 uv) {
    return cerp(
        cerp(p0, p1, p2, p3, uv.x),
        cerp(p4, p5, p6, p7, uv.x),
        cerp(p8, p9, p10, p11, uv.x),
        cerp(p12, p13, p14, p15, uv.x),
        uv.y
    );
}

void mainImage( out vec4 fragColor, in vec2 xy )
{
    vec2 uv = xy/iResolution.xy;


    float res = 20.;
    vec2 uvff = floor((uv-1./res)*res)/res;
    vec2 uvf = floor(uv*res)/res;
    vec2 uvc = ceil(uv*res)/res;
    vec2 uvcc = ceil((uv+1./res)*res)/res;
    vec3 p0 = noise(uvff);
    vec3 p1 = noise(vec2(uvf.x, uvff.y));
    vec3 p2 = noise(vec2(uvc.x, uvff.y));
    vec3 p3 = noise(uvcc);
    vec3 p4 = noise(vec2(uvff.x, uvf.y));
    vec3 p5 = noise(vec2(uvf.x, uvf.y));
    vec3 p6 = noise(vec2(uvc.x, uvf.y));
    vec3 p7 = noise(vec2(uvcc.x, uvf.y));
    vec3 p8 = noise(vec2(uvff.x, uvc.y));
    vec3 p9 = noise(vec2(uvf.x, uvc.y));
    vec3 p10 = noise(vec2(uvc.x, uvc.y));
    vec3 p11 = noise(vec2(uvcc.x, uvc.y));
    vec3 p12 = noise(vec2(uvff.x, uvcc.y));
    vec3 p13 = noise(vec2(uvf.x, uvcc.y));
    vec3 p14 = noise(vec2(uvc.x, uvcc.y));
    vec3 p15 = noise(vec2(uvcc.x, uvcc.y));
    vec3 val = bcerp(p0, p1, p2, p3, p4, p5, p6, p7, p8, p9, p10, p11, p12, p13, p14, p15, mod(uv, 1./res)*res );

    fragColor = vec4(val, 0.);
}
