void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
  vec2 m = -1.0 + 2.0 * iMouse.xy / iResolution.xy;

  float a1 = atan(p.y-m.y,p.x-m.x);
  float r1 = sqrt(dot(p-m,p-m));
  float a2 = atan(p.y+m.y,p.x+m.x);
  float r2 = sqrt(dot(p+m,p+m));
 float a3 = atan(p.y+m.y/2.0,p.x+m.x);

  vec2 uv;
  uv.x = 0.12*clamp(sqrt(exp(iTime)),exp(r1-r2)*0.55,a2+r1+a2*iTime) + (r1-r2)*0.55 + sin(exp(iTime/r2));

  uv.y = asin(sin(a1-a2-a3))/3.1416;


  vec3 col = texture( iChannel0, 0.125*uv ).zyx;

  float w = exp(-55.0*r1) + exp(-55.0*r2*r2);

  w += 0.25*smoothstep( 0.03,1.0,sin(228.0*uv.x));
  w += 0.25*smoothstep( 0.93,1.0,sin(128.0*uv.y));

  fragColor = vec4(fract(sqrt (col+w) +(col+w) / 1.1) ,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.5,0.0,1.0);
}
