void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - vec2(.5)) * vec2(1.,1.15) + vec2(.5);
    vec3 col = vec3(clamp(log(1.+texture(iChannel0, fract(uv)).x),0.,1.));
    if (usePalette) {
        col = pal(col.x*.7+.2, PAL);
    }
    fragColor = vec4(col, 1.);
}

#define T(uv) texture(iChannel1, fract((uv)/iResolution.xy)).x

#define PI 3.141592653589793238462643383

const float an = 60.;
const vec2 dir0 = vec2(cos(an * PI/180.), sin(an * PI/180.));

#define CTRL   17.
#define SPACE  32.

bool isPressed(float keyCode)
{
    keyCode = (keyCode + 0.5) / 256.0;
    vec2 uv = vec2(keyCode, 0.25);
    float key = texture(iChannel2, uv).r;

    return key > 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord/iResolution.xy;
    vec2 tuv0 = fragCoord;
    vec2 ratio = vec2(1.,iResolution.y/iResolution.x);
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 uv = (uv0 - vec2(.5)) * ratio;
    float col;

    vec2 step0 = 0.5/iResolution.xy;
    vec2 mc = abs(uv0-mouse)*ratio;
    float ptSize = length(step0);

    float u_th = texture(iChannel1, uv0).x;
    bool isCtrlPressed = isPressed(CTRL);

    if (iFrame == 0 || isPressed(SPACE)) {
        col = length(uv) < ptSize ? 2. : beta;
    }
    else if (iMouse.z > 0. && u_th > 0. && isCtrlPressed) {
        col = mouse.y;// * .5;
    }
    else if (iMouse.z > 0. && dot(mc, dir0) < 0.2 && dot(mc, vec2(1.0,0.)) < 0.2 &&
            !isCtrlPressed)
    {
        col = length(mc) < ptSize ? 2. : beta;
    }
    else {
        vec2 step1 = vec2(2.0);
        vec2 step2 = vertical ? vec2(0., step1.y) : vec2(step1.x, 0.);
        vec2 dir = vertical ? vec2(2.,1.) : vec2(1.,2.);

        float ul = T(tuv0 - dir);
        float ur = T(tuv0 + vec2(dir.x, -dir.y));
        float dr = T(tuv0 + dir);
        float dl = T(tuv0 + vec2(-dir.x, dir.y));
        float  r = T(tuv0 + step2);
        float  l = T(tuv0 - step2);

        float u = texture(iChannel0, uv0).x;
        float u2 = u_th == 0. ? u + gamma * mouse.x : 0.;
        float sum = ul + ur + r + dr + dl + l;
        float u1 = u_th + alpha*(-6.*u_th+sum)/12. + u2;
        col = u1;
    }
    fragColor = vec4(vec3(col),1.);
}

// Threshold

#define T(uv) texture(iChannel0, fract((uv)/iResolution.xy)).x

const float th0 = 1.0;

// Hash without sine by Dave_Hoskins
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord/iResolution.xy;
    vec2 tuv0 = fragCoord;
    vec3 col;

    col = vec3(0.0);
    vec2 step1 = vec2(2.0);
    vec2 step2 = vertical ? vec2(0., step1.y) : vec2(step1.x, 0.);
    vec2 dir = vertical ? vec2(2.,1.) : vec2(1.,2.);

    float ul = T(tuv0 - dir);
    float ur = T(tuv0 + vec2(dir.x, -dir.y));
    float dr = T(tuv0 + dir);
    float dl = T(tuv0 + vec2(-dir.x, dir.y));
    float  r = T(tuv0 + step2);
    float  l = T(tuv0 - step2);

    float u = texture(iChannel0, uv0).x;
    float th = th0;
    if (randomize) th += -.05 + hash11(iTime) * 0.1;
    bool ulb = ul > th;
    bool urb = ur > th;
    bool  lb =  l > th;
    bool dlb = dl > th;
    bool drb = dr > th;
    bool  rb =  r > th;
    int b = int(ulb) + int(urb) + int(lb) + int(dlb) + int(drb) + int(rb);
    if (u > th || b > 0) u = 0.;
    col = vec3(u);

    fragColor = vec4(col,1.);
}

// Emboss

#define T(uv) texture(iChannel0, fract((uv)/iResolution.xy)).x

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 tuv0 = fragCoord;

    //float ul = T(tuv0 + vec2(-1.,-1.));
    float  u = T(tuv0 + vec2(0.,-1.));
    float  l = T(tuv0 + vec2(-1.,0.));
    //float ur = T(tuv0 + vec2(1., -1.));
    float  r = T(tuv0 + vec2(1., 0.));
    //float dr = T(tuv0 + vec2(1., 1.));
    float  d = T(tuv0 + vec2(0., 1.));
    //float dl = T(tuv0 + vec2(-1., 1.));
    //float col = (ur + r + dr + d + dl - ul - u - l) * .5;
    float col = (r + u + T(tuv0) - d - l);

    fragColor = vec4(vec3(col),1.0);
}
