#define R iResolution.xy
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

#define Ta(u) texture(iChannel0,u)
#define Tb(u) texture(iChannel1,u)
#define Tc(u) texture(iChannel2,u)
#define Td(u) texture(iChannel3,u)

float luma(vec4 color) {
  return dot(color.rgb, vec3(0.299, 0.587, 0.114));
}

float hash11(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

