// Deriv

void mainImage( out vec4 C, in vec2 U ){
    vec2 uv = U/R.xy;

    vec2 st = 2./R.xy;


    C = vec4(
        luma(Ta(uv + vec2(st.x,0))) - 
        luma(Ta(uv - vec2(st.x,0))) +
        luma(Ta(uv + vec2(0,st.y))) - 
        luma(Ta(uv - vec2(0,st.y)))  
    );
}

// Worm

void mainImage( out vec4 C, in vec2 U ){
    //vec2 uv = U/R.xy*0.;

    if(U.x < 1.){

        vec2 startp = vec2(hash11(iTime*110.1 + 20.)*1.,0);
        startp *= 0.;
        if(iMouse.z > 0.)
            startp.x = iMouse.x/iResolution.x;
        vec2 p = startp;

        vec2 prevHeadDir = vec2(0,1);
        for(float y = 0.; y < R.y; y++){

            const float rotIters = 20.;
            float minD = 1000.;
            vec2 headDir = vec2(0);

            float headLen = (20. + sin(y + iTime)*20.*0.)/R.y;

            for(float rotIter = 0.; rotIter < rotIters; rotIter++){
                float rotRad = 1.5 + sin(iTime)*1.;
                rotRad *= 0.4;
                vec2 dir = vec2(0,1) * rot( rotIter/rotIters*rotRad - rotRad*0.5) ;
                vec2 lheadUv = p + dir* headLen;
                float sampleD = Td(lheadUv).x;

                if(sampleD <= minD){
                    minD = sampleD;
                    headDir = dir;
                }
            }

            const bool smoot = true;
            const float smoothAmt = 1.;
            if(smoot){
                headDir = mix(prevHeadDir,headDir,smoothAmt*abs(dot(prevHeadDir,headDir)));
                headDir = normalize(headDir);

            }
            prevHeadDir = headDir;

            // raymarching HAHAHA 
            float t = 0.;
            vec2 rmDir = headDir;
            vec2 rp = p;
            {
                rp.y = -1./R.y;
                for(float i = 0.; i < 20.; i++){
                    float d = rp.y;
                    if(d > 0.){
                        break;
                    }
                    d = abs(d);
                    d *= 0.9;
                    rp += rmDir*d;
                    t += d;
                }
            }

            p.x = rp.x;
            //p.x += t * rmDir.x*0.4;
            //p.x += abs(sin(y*4.))*0.0004;
            p.y += 1./R.y;


            if(y == floor(U.y)){
                C = vec4(p.x);
                break;
            }
        }

    }
}

// pixelsort?

void mainImage( out vec4 C, in vec2 U ){
    vec2 uv = U/R.xy;

    float x = Tc(uv*vec2(0,1)).x;
    float wrm = texelFetch(iChannel2,ivec2(0,U.y),0).x;

    if(uv.x < x && (iMouse.z > 0.))
        C = mix(
            Td(uv),
            Td(uv - vec2(1./R.x,0)),
            clamp(abs(uv.x - wrm)*R.x*0.2,0.,1.)
            );
    else
        C = Td(uv);


    if(iFrame < 2)
        C = Ta(uv);
}
