// ========================= //
// nethe550
// Metaballs
// ========================= //

#ifdef GL_ES
    precision highp float;
#endif

#define scale 0.5
#define time iTime * 0.0125
#define aberration 5.0 * min(sin(time * sign(time) * 50.0), 0.0)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    uv += 0.05;
    uv *= scale;

    float distToCenter = length(uv);

    vec3 col = vec3(0.0);

    for (int i = 0; i < 3; i++) {
        uv += uv / distToCenter * aberration;
		col[i] = 0.01 / length(fract(uv * 0.125) - 0.5);
    }

    fragColor = vec4(col / distToCenter, 1.0);

}

vec2 random(vec2 p) {
    return fract(sin(vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)))) * 43758.5453);
}
