int max_i = 500;

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float z = 5.0;

    float greater = iResolution.y;
    if(iResolution.x > iResolution.y) greater = iResolution.x;

    float initial_real = ( (fragCoord.x / greater) - (iResolution.x/greater)/2.0 ) * z;
    float initial_img = ( ( fragCoord.y / greater) - (iResolution.y/greater)/2.0 ) * z;

    float current_real = initial_real;
    float current_img = initial_img;

    int i = 1;
    while(i < max_i){
        i++;
        float next_real = (current_real * current_real) - (current_img * current_img);
        float next_img = current_real * current_img * 2.0;

        current_real = next_real + initial_real;
        current_img = next_img + initial_img;

        if(abs(current_real + current_img) > 10.0){
            break;
        }
    }
    if(i == max_i){
        fragColor = vec4( .0, .0, .0, 1.0);
    } else {
        float v = float(i)/float(max_i) * 10.0;
        fragColor = vec4( v, v, v, 1.0);
    }
}
