#define N 2

vec2 circle(float t) {
    return vec2(cos(t), sin(t));
}

vec3 image(vec2 p) {
    p *= 1.3;
    float t = iTime * .5;
    vec3 col = vec3(1.);
    if (length(p) < 1.) {
        return vec3(0.);
    }
    if (length(p - circle(t) * .15 * (3. + sin(t * 2.)) / 3.) < 1.) {
        col = vec3(.5, 0., 0.);
    }
    if (length(p - circle(1.01 * t + 3.) * .17 * (2. + sin(t)) / 2.) < 1.) {
        col = .2 * col + .8 * vec3(1., .7, 0.);
    }
    if (length(p - circle(-1.02 * t + 5.) * .19) < 1.) {
        col = .1 * col + .9 * vec3(.7, .1, 0.5);
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    float d = 1. / min(iResolution.x, iResolution.y);  //float(2*N+1);
    vec3 col = vec3(0.);
    for (int i = -N; i <= N; ++i) {
        for (int j = -N; j <= N; ++j) {
            col += image(uv + d * vec2(i, j));
        }
    }
    col /= float((2 * N + 1) * (2 * N + 1));
    fragColor = vec4(col, 1.0);
}
