const vec3 one = vec3(55., 86., 32.)/255.;
const vec3 two = vec3(109., 142., 52.)/255.;
const vec3 three = vec3(129., 183., 12.)/255.;
const vec3 four = vec3(205., 219., 87.)/255.;

float random (vec2 _st) {
    return fract(sin(dot(_st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = st;
    uv.x *= iResolution.x/iResolution.y;
    uv.x += iTime * .25;
    uv *= 10.;
    vec2 ipos = floor(uv);

    //put each square in a random group 1 <-> 4
    float randVal = random(ipos);
    float timeGroup;
    if (randVal > .75) timeGroup = 0.;
    else if (randVal > .5) timeGroup = 1.;
    else if (randVal > .25) timeGroup = 2.;
    else timeGroup = 3.;

    //randomize each group one at a time
    float timeVal = floor((iTime*12.+(1.*timeGroup))*.25);
    randVal = random(vec2(ipos.x + timeVal, ipos.y + timeVal));

    //assign color to square
    vec3 randColor;
    if (randVal > .75) randColor = one;
    else if (randVal > .5) randColor = two;
    else if (randVal > .25) randColor = three;
    else randColor = four;

    //add darkness on edges
    col = randColor * (1.-abs(st.x-.5)*1.5)*(1.-abs(st.y-.5)*1.5);

    fragColor = vec4(col,1.);
}
