//Ethan Alexander Shulman xaloez.com 2021 CC0 public domain

/*detail size in pixels, higher value is more accurate to source texture
but takes longer to resolve noise*/
#define DETAIL_SIZE 2


//uvec4 seed, returns uvec4 hash
uvec4 hash(uvec4 seed) {
    uvec4 h = (0x6A7F8FAAu^seed)*0x01000193u;
    h = ((h.wxyz>>3u)^h^seed.yzwx)*0x01000193u;
    h = ((h.zwxy>>8u)^h^seed.wxyz)*0x01000193u;
    return h^(h>>11u);
}
#define I2F (1./float(0xFFFFFFFFu))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define SS iChannelResolution[1]
    uvec4 hv = hash(uvec4(fragCoord.xyy,iDate.w*100.));
    if (iFrame < 60 || iMouse.w > 0.) {
        //initialize
        fragColor = vec4(hv.xy%uvec2(SS.xy),0,1);
        return;
    }

    float mdst = 0.;
    vec2 sc = texelFetch(iChannel0,ivec2(fragCoord),0).xy,
        mc = sc, oscale = vec2(1u+hv.zw%uint(DETAIL_SIZE));
    vec4 sv = pow(texelFetch(iChannel1,ivec2(sc.xy),0),vec4(2.2));
    for (float x = -1.; x < 2.; x++) {
        for (float y = -1.; y < 2.; y++) {
            vec2 off = vec2(x,y)*oscale,
                px = fragCoord+off;
            if (min(px.x,px.y) < 0. || max(px.x-iResolution.x,px.y-iResolution.y) >= 0. ||
                (x ==0. && y == 0.)) continue;

            vec2 oc = texelFetch(iChannel0,ivec2(px),0).xy,
                c0 = sc+off, c1 = oc-off;
            if (min(c0.x,c0.y) < 0. || max(c0.x-SS.x,c0.y-SS.y) >= 0. ||
               min(c1.x,c1.y) < 0. || max(c1.x-SS.x,c1.y-SS.y) >= 0.) continue;  
            vec4 ov = pow(texelFetch(iChannel1,ivec2(oc),0),vec4(2.2));
            float dst = length(ov.xyz-pow(texelFetch(iChannel1,ivec2(sc+off),0).xyz,vec3(2.2)))-
                        length(sv.xyz-pow(texelFetch(iChannel1,ivec2(oc-off),0).xyz,vec3(2.2)));
            if (dst > mdst) {
                mc = oc-off;
                mdst = dst;
            }
        }
    }

    fragColor = vec4(mc,0,1);
}
