// --- stub task: draw a circle around the changing pixels

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);                             // show stub evolving image

    if (  U.x/4. < R.x/64. && U.y/4. < R.y/64. )
        O =  T1(U/4.), O.b += .3;         // show the 4096x reduced stream


    for (float j = 0.; j < R.y/64.; j++ ) // read the 4096x reduced stream
        for (float i = 0.; i < R.x/64.; i++ ) {
            vec4 T =  T1(vec2(i,j));
            if ( T.a != 0. )              // change detected
                O[int(T.z)] += smoothstep(1.5,0.,abs(length(U-T.xy)-10.) ); // draw circle
        }
}

// --- stub task: revert pixel at random locations

#define H(p)  fract(sin(dot(p, vec2(12.9898, 78.233))-iTime) * 43758.5453)

void mainImage( out vec4 O, vec2 U )
{
    O = T(U) * vec4(1,.99,.99,1); 
    O.a = O.r;                     // memorize the previous value
    if ( H(U)*H(U*.973) > .995 ) O.rgb += 1.-O.rgb;
}

// --- first reduction by 64: each thread detect 1st change in a 8x8 block

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    if( U.x > R.x/8. || U.y > R.y/8. ) return; // image/64

    O-=O;
    for (int k = 0; k < 64; k++ ) {
        vec2 V =  8.*U + vec2(k%8,k/8);
        vec4 T = T( V );
        if ( T.r != T.a ) 
          { O = vec4( V, T.r, 1); return; } // keep location + value, flag to 1
    }    
}

// --- first reduction by 64: each thread detect a flag set in a 8x8 block

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    if( U.x > R.x/64. || U.y > R.y/64. ) return; // image/4096
    O-=O;
    for (int k = 0; k < 64; k++ ) {
        vec2 V =  8.*U + vec2(k%8,k/8);
        vec4 T = T( V );
        if ( T.a != 0. ) { O = T; return; } // propagate the value
    }    
}
