const float pi = acos(-1.0);
const float twopi = pi*2.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x *= iResolution.x/iResolution.y;


   float leftside  = abs(dot(vec2(sin(-(pi+.6)),cos(-(pi+.6))),vec2(uv)));
    float rightside = abs(dot(vec2(sin((pi+.6)),cos((pi+.6))),vec2(uv)));

    float sides = max(leftside,rightside);
    float rotup = dot(vec2(sin(pi),cos(pi)),vec2(uv));    
    float rotsides = dot(vec2(sin(pi * .5),cos(pi * .5)),vec2(uv)) * 4.5;
    float res = max(max(sides ,max(rotup,abs(rotup) * 4.5) *.1) *4.7,
    abs(rotsides));
    vec4 hex = (smoothstep(.51121,.53333,res) )* vec4(1.0);

    if(length(hex)< .56654)
    fragColor =vec4(.3444,.2,1.0,.0);
    else
    fragColor = vec4(hex) * vec4(241321,.3454,.413333,1.0);
}
