vec2 alterPosition (vec2 pos, vec2 mouse)
{
    // rotate 180
    if (true) {
        mat2 m = mat2(-1.0, 0.0,
                      0.0, -1.0);
        pos = pos*m + vec2(1.0,1.0);
        return pos;
    }

    // rotate
    if (false) {
        vec2 center = vec2(0.5,0.5);
        mat2 m = mat2(cos(iTime), -sin(iTime),
                      sin(iTime), cos(iTime));
        return (pos-center)*m + center;
    }

    // mirror
    if (false) {
        if (pos.x > mouse.x)
            pos.x = 1.0 - pos.x;
        return pos;
    }

    return pos;
}

vec4 alterPixel (vec4 pixel, vec2 mouse)
{
    return pixel;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec2 pos = fragCoord.xy / iResolution.xy;

    pos = alterPosition(pos, mouse);
    vec4 c = texture(iChannel0,pos);

	fragColor = alterPixel(c, mouse);
}
