void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    float count = 0.;

    for (float i = 0.; i < iResolution.y; i+=1.)
    {
        col += texture(iChannel0,vec2(uv.x,i/iResolution.y)).rgb;
        count += 1.;
    }
    col /= count;
    float p = (col.r+col.g+col.b)/3.;

    p -= 0.5;
    uv - 0.5;
    p -= uv.y - 0.5;
    p = min(smoothstep(-0.02,-0.01,p),smoothstep(0.02,0.01,p));

    vec4 cOut = mix(vec4(p,p,p,1.0),texture(iChannel0,uv),0.5);

    fragColor = cOut;
}
