void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( 2.*u - R ) / R.y;
 // U.x *= 1. + .5*sin(iTime);
    U.y = fract(U.y) 
        + .01* sign( (fract(2.*U.x)-.5)*(mod(U.y,2.)-1.) );

    O = vec4( mix(.5, step(fract(U.x),.5), smoothstep( 9./R.y, 0., abs(U.y-.5)-.01 ) ));
}
