const float PI = 3.1416;
const float DIST = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 noiseSample1 = texture(iChannel1, (uv * 0.05) + vec2(iTime * 0.01, 0.0));
    vec4 total = vec4(0.0);
    float creaseMask = (1.0 - texture(iChannel0, uv).r);
    float radius = DIST * noiseSample1.r * noiseSample1.r * noiseSample1.r * creaseMask * creaseMask * creaseMask;

    for (int i = 0; i < 16; i++){
        float rad = (PI * float(i)) / 8.0;
        vec2 samplePoint = vec2(cos(rad) * radius, sin(rad) * radius);
        vec4 texSample = texture(iChannel0, uv + samplePoint);
        total += texture(iChannel0, uv + samplePoint);
    }

    total /= 16.0;

    vec4 original = texture(iChannel0, uv);
    vec4 diff = total - original;

    // Output to screen
    fragColor = original - (diff * 3.0);
}
