void mainImage(out vec4 color, in vec2 coord)
{
  int n = int(log(float(max(iResolution.x,iResolution.y)))/log(2.));
  int k = 0;
  int x = int(coord.x);
  int y = int(coord.y);

  int z = x & y;
  for(int i=0; i<n; ++i) {
    k += (z>>i)&1;
  }
  float d = float(k)/float(n);
  color = vec4(1.-2.*d, 1.-2.*d, 2.-2.*d, 1.);
}
