// Modeling 
// Camille Daclin 


const int Steps = 100;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon



// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(vec2 m,vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.5;

  // Origin
  ro=vec3(37.,0.,15.);
  ro*=Rz(a);

  // Target point
  vec3 ta=vec3(0.,0.,1.);

  // Orthonormal frame
  vec3 w=normalize(ta-ro);
  vec3 u=normalize(cross(w,vec3(0.,0.,1.)));
  vec3 v=normalize(cross(u,w));
  rd=normalize(p.x*u+p.y*v+le*w);
}



// Primitives


// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p,vec3 c,float r)
{
  return length(p-c)-r;
}

// Boite
// p : point
float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// Tore
float Tore( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

//Cylindre
//p : point
// h : height
// r : radius
float Cylindre( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cyl2(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}


//Capsule
// p : point
// h : height
// r: radius
float Capsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

// Plan
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}



//Operateurs
//Rotation
vec3 rotateX(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Matrice de rotation selon l'axe Y
vec3 rotateY(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Matrice de rotation selon l'axe Z
vec3 rotateZ(vec3 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}
//Homoth√©tie

vec3 homo(vec3 p,float k)
{
    return p*k;
}

//Translation
vec3 translation(vec3 p,vec3 t)
{
    return p-t;
}

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
    return min(a,b);
}

//Difference
float diff(float a,float b)
{
    return max(a,-b);
}
// Intersection
// a,b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}

// Colonne
//p : point
float colonne(vec3 p)
{
      float h = 1.5;
      vec3 r1 = translation(p,vec3(6,-8,-1.5));
      vec3 r11 = homo(r1,h);
      float a1  = box(r11,vec3(3,3,1));
      vec3 q1 = translation(p,vec3(6,-8,6));
      vec3 q11 = homo(q1,h);
      float v1 = box(q11,vec3(3,3,1));

      vec3 y1 = rotateX(p,1.57);
      vec3 y11 = homo(y1,h);
      vec3 y21 = translation(y11,vec3(9,-3.4,-12));
      float e1 = Cylindre(y21,1.70,6.0);
      float g1 = Union(a1,Union(v1,e1));

      vec3 r2 = translation(p,vec3(-6,-8,-1.5));
      vec3 r12 = homo(r2,h);
      float a2  = box(r12,vec3(3,3,1));
      vec3 q2 = translation(p,vec3(-6,-8,6));
      vec3 q12 = homo(q2,h);
      float v2 = box(q12,vec3(3,3,1));

      vec3 y2 = rotateX(p,1.57);
      vec3 y12 = homo(y2,h);
      vec3 y22 = translation(y12,vec3(-9,-3.4,-12));
      float e2 = Cylindre(y22,1.70,6.0);
      float g2 = Union(a2,Union(v2,e2));

      vec3 r3 = translation(p,vec3(-6,8,-1.5));
      vec3 r13 = homo(r3,h);
      float a3  = box(r13,vec3(3,3,1));
      vec3 q3 = translation(p,vec3(-6,8,-6));
      vec3 q13 = homo(q3,h);
      float v3 = box(q13,vec3(3,-3,1));

      vec3 y3 = rotateX(p,1.57);
      vec3 y13 = homo(y3,h);
      vec3 y23 = translation(y13,vec3(9,-3.4,12));
      float e3 = Cylindre(y23,1.70,6.0);
      float g3 = Union(a3,Union(v3,e3));

      vec3 r4 = translation(p,vec3(6,8,-1.5));
      vec3 r14 = homo(r4,h);
      float a4  = box(r14,vec3(3,3,1));
      vec3 q4 = translation(p,vec3(6,8,6));
      vec3 q14 = homo(q4,h);
      float v4 = box(q14,vec3(3,-3,1));

      vec3 y4 = rotateX(p,1.57);
      vec3 y14 = homo(y4,h);
      vec3 y24 = translation(y14,vec3(-9,-3.4,12));
      float e4 = Cylindre(y24,1.70,6.0);
      float g4 = Union(a4,Union(v4,e4));
      return Union(g1,Union(g2,Union(g3,g4)));

    }

//Base
// p : point
float base(vec3 p)
{
    float a = 4.0;
    float b = 1.0;
    float c = 0.25;
    vec3 p1 = translation(p,vec3(0,10.5,-2.5));
    float g1 = box(p1,vec3(a,b,0.59));
    vec3 p2 = translation(p,vec3(0,12.0,-3.5));
    float g2 = box(p2,vec3(a,b,0.59));
    vec3 p3 = translation(p,vec3(0,13.5,-5.0));
    float g3 = box(p3,vec3(a,b,0.59));
    vec3 p4 = translation(p,vec3(0,11,-5.5));
    float g4 = box(p4,vec3(a,1.5,2.5));


    vec3 p11 = translation(p,vec3(0,0,-3.5));
    float b1 = box(p11,vec3(8,10,1.59));
    return Union(b1,Union(g4,Union(g1,Union(g2,g3))));
}

//Toit
// p : point
float toit(vec3 p)
{
    vec3 p11 = rotateZ(p,1.57);
    vec3 p1 = rotateX(p11,0.3);
    vec3 p2 = translation(p1,vec3(0,0,10.5));
    float b1 = box(p2,vec3(10,10,1.59));
    vec3 p33 = rotateZ(p,1.57);
    vec3 p3 = rotateX(p33,-0.3);
    vec3 p4 = translation(p3,vec3(0,0,10.5));
    float b2 = box(p4,vec3(10,10,1.59));
    vec3 p55 = rotateZ(p,1.57);
    vec3 p5 = translation(p55,vec3(0,0,7.9));
    float b = box(p5,vec3(10,10,1.59));


    vec3 p22 = translation(p,vec3(0,10,7.7));
    return Union(Tore(p22,vec2(1,0.5)),diff(b,Union(b1,b2)));

}

// Colonne 2
// p : point
float col2(vec3 p)
{
    vec3 p1 = translation(p,vec3(0,-8,0));
    float c1 = cyl2(p1,vec3(0,0,-2),vec3(0,0,7),1.7);
    return c1;
}

// Mur 
// p : point
float mur(vec3 p)
{
    float e = col2(p);
     float c = colonne(p);
     return Union(e,c);
}


// Monument
// p : point
float Monument(vec3 p)
{
    float b = base(p);
    float s = toit(p);
    float mur = mur(p);
    return Union(b,Union(s,mur));
}


// p : point
float object(vec3 p)
{

        float M = Monument(p);


  float v = Union(M,Intersection(Plane(p,vec3(0.0,0.0,1.0),vec3(0.0,0.0,-4.0)),Sphere(p,vec3(0.0,0.0,-4.0),25.0)));

  return v;
}




// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps = 0.001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

  // Start at the origin
  float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v < 0.0)
      {
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Lighting



// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.45,0.55,0.99),vec3(0.65,0.69,0.99), d.z*0.5+0.5);
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p+0.1*n,l,100.0,h,s);
    if (!h)
    {
      return 1.0;
    }
    return 0.0;
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    // Ambient color
    vec3 ambient = 0.25+0.25*background(n);


    // Shadow computation
    float shadow = Shadow(p,n,l);

    // Phong diffuse
    vec3 diffuse = 0.35  * clamp(dot(n, l),0.0,1.0) * vec3(1.0,1.0,1.0);

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = 0.15 * pow(clamp(dot(r,l),0.0,1.0),30.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + shadow * (diffuse + specular);
    return c;
}

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t = float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p = (-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip = true;
    }
       else
       {
           pip = false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s);
  }


  color=vec4(rgb, 1.0);
}
