#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p=vec3(log2(length(p))-tan(iTime),exp2(p.z),atan(p.x,p.y));
    q.xz*=R2D(iTime);

    for(j=0.;j++<8.;)
        p=abs(mix(p,q,.08)-.1)-.2,
        d=max(max(p.z,p.x),p.z),
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(exp(p*q),1)*3./i;;
}
