void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    float rainbowSpeed = 1.0; // Adjust this value to control the speed of the rainbow effect

    // Rainbow effect
    float timeOffset = iTime * rainbowSpeed;
    float frequency = 3.0;
    float phaseShift = 2.0;
    float amplitude = 0.5;

    float rainbowFactor = sin((uv.x + timeOffset) * frequency + phaseShift) * amplitude + 0.5;
    // Ensure rainbowFactor stays within the range [0, 1]
    rainbowFactor = clamp(rainbowFactor, 0.0, 1.0);

    // Calculate rainbow color
    vec3 rainbowColor = vec3(0.0);
    if (uv.y > 0.0 && uv.y < 1.0) {
        rainbowColor = mix(vec3(1.0, 0.0, 0.0), vec3(1.0, 1.0, 0.0), smoothstep(0.0, 1.0, rainbowFactor));
        rainbowColor = mix(rainbowColor, vec3(0.0, 0.0, 1.0), smoothstep(1.0, 0.0, rainbowFactor));
    }

    fragColor = vec4(rainbowColor, 1.0);
}
