void mainImage(out vec4 O, vec2 F) {
    vec2 g = iResolution.xy,
         o = (F+F-g)/g.y/.7; 
    float l = 0., 
          f = iTime*.99-2.;

    for (O *= l; l++ < 55.; O += 
        .011/max(abs(o.x + cos(l*(cos(f*.5)*.5+.6)+f)), 
                 abs(o.y + sin(l+f)))-
        (sin(l+f*4.)*.04+.02)*(cos(l+max(abs(o.x),abs(o.y))*4.+vec4(0,1,2,0))+1.));
}
