vec2 random(vec2 uv){
    uv = vec2( dot(uv, vec2(127.1,311.7) ),
               dot(uv, vec2(269.5,183.3) ) );
    return -1.0 + 2.0 * fract(sin(uv) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y * .55;
    float shade1 = sin(uv.x * 10.0 + iTime);
    float shade2 = sin(uv.y * 50.0 + iTime * 2.0);
    uv.y += shade1 * .025;
    uv.x += shade2 * .0025;
    float freq = 50.0;
    vec3 col =vec3(0.298,0.482,0.784);
    float strip1 = step(.925, pow(sin(uv.y * freq * .5 + .5 * 3.14159 ), 2.0));
    float strip2 = step(1.9999, pow(sin(uv.x * freq * 2.0) + sin(uv.x * freq),2.0)) + 
           step(1.9999, pow(sin(uv.y * freq * 2.0) + sin(uv.y * freq),2.0));
    float strip3 = step(1.9999, pow(sin(uv.x * freq * 2.0) + sin(uv.x * freq),2.0)) * 
           step(1.9999, pow(sin(uv.y * freq * 2.0) + sin(uv.y * freq),2.0));

    float m1 = smoothstep(-.075,1.375, abs(fract(uv.y*550.0) - (.4 * sin(uv.x * 800.0) * .5 + .5)));
    float m2 = smoothstep(-.055,1.375, abs(fract(uv.y*550.0) - (.4 * sin(uv.x * 800.0 + 1.2) * .5 + .5)));
    float m3 = smoothstep(-.8, .8, sin(uv.x * 10.0 + atan(uv.y * 10.0)*10.0) * sin(uv.y * 10.0));

    col = mix(col, vec3(0.282,0.306,0.616), strip1);
    col = mix(col, vec3(0.843,0.937,0.976), strip2);
    col = mix(col, vec3(0.794,0.455,0.698), strip3);
    col = mix(col, vec3(0.243,0.282,0.376), m1 * .645);
    col = mix(col, vec3(0.635,0.165,0.165), m2 * .645);
    col += m3 * .055;
    //col += sin(uv.x * 10.0 + 3.14159 / 2.0) * .095;
    col *=(1.25- length(uv));
    col.r = clamp(0.0, 1.0, col.r);
    col.g = clamp(0.0, 1.0, col.g);
    col.b = clamp(0.0, 1.0, col.b);
    col += -.05;
    col += random(uv).x * .05;
    //col -= sin(uv.y * 1000.0) * .005;
    col *= vec3(0.984,0.914,0.718);
    shade1 = sin(uv.x * 10.0 + iTime);
    shade2 = sin(uv.y * 50.0 + .29);
    col += shade1 * .08;
    col += shade2 * .08;
    fragColor = vec4(col,1.0);
}
