// Texturing and noise 

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}


// Fonction de mouvement Brownien fractionnaire
float MouvementBrownien(vec3 p, int octaves, float lacunarity, float gain)
{
    float sum = 0.0;
    float amplitude = 1.0;
    float frequency = 1.0;

    for (int i = 0; i < octaves; ++i)
    {
        sum += amplitude * Noise(p * frequency);
        frequency *= lacunarity;
        amplitude *= gain;
    }

    return sum;
}

// Fonction de turbulence
float Turbulence(vec3 p, int octaves, float lacunarity, float gain)
{
    float sum = 0.0;
    float amplitude = 1.0;
    float frequency = 1.0;

    for (int i = 0; i < octaves; ++i)
    {
        sum += abs(amplitude * Noise(p * frequency));
        frequency *= lacunarity;
        amplitude *= gain;
    }

    return sum;
}

// Compute the distance to the Voronoi boundary
// x : Point
// Return (closest distance, second closest, cell id)
vec3 Voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + Hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

// Camera -----------------------------------------------------------------------

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3(35.0,0.0,15.0);
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}

// Texturing
// Eric Galin

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
vec3 c;
float s;
};

// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : point
// c : center 
// r : radius
V Sphere(vec3 p, vec3 c,float r,int index)
{
  return V(length(p-c)-r,index);
}

// Cube 
// p : point
// c : center 
// r : radius
V Cube(vec3 p,vec3 c,float r,int index)
{
   vec3 q = abs(p-c) - vec3(r);
  return V(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0),index);
}

// Plane 
// p : point
// n : Normal of plane
// o : Point on plane
V Plane(vec3 p, vec3 n, vec3 o,int index)
{
    return V(dot((p-o),n),index);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

// Dice 
// p : point
// c : center 
// r : radius
V Dice(vec3 p,vec3 c,float r,int index)
{
  return Intersection(Cube(p,c,r*0.75,index),Sphere(p,c,r,index));
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V u = Plane(p,normalize(vec3(0.0,0.0,1.0)),vec3(0.0,0.0,-4.0),0);

  u=Union(u,Dice(p,vec3( 0.0, 0.0, 2.0),2.0,1));
  u=Union(u,Dice(p,vec3( -10.0, 0.0, 2.0),2.0,2));
  u=Union(u,Dice(p,vec3( 10.0, 0.0, 2.0),2.0,3));
  u=Union(u,Dice(p,vec3( 0.0, 10.0, 2.0),2.0,4));
  u=Union(u,Dice(p,vec3( 10.0, 10.0, 2.0),2.0,5));
  u=Union(u,Dice(p,vec3( -10.0, 10.0, 2.0),3.0,6));
  u=Union(u,Dice(p,vec3( 10.0, -10.0, 2.0),3.0,7)); 
  u=Union(u,Dice(p,vec3( -10.0, -10.0, 2.0),3.0,8)); 
  return u;

  //V u = Sphere(p, vec3(0.0, 0.0, 2.0), 4.0, 6);
  //return u;
}
// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.65,0.75,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


// Shading and lighting ---------------------------------------------------------------------------

T Texture0(in vec3 p) //au sol
{
  return T(vec3(0.84,0.84,0.84),0.25);   
}

T Texture1(in vec3 p) //dé du milieu
{
  return T(vec3(1.,1.,1.),0.25);   
}

T Texture2(in vec3 p) // dé de droite (vert)
{
  return T(vec3(1.,0.,0.),0.25);   
}

T Texture3(in vec3 p) //dé de gauche (cyan)
{
  return T(vec3(0.,0.,1.),0.25);   
}


// Fonction de texture uniforme
T UniformTexture(in vec3 p)
{
    // Couleur désirée
    vec3 color = vec3(0.5, 0.5, 0.5); // Par exemple, gris
    float s = 0.25; // modifiable
    return T(color, s);
}

// Fonction de texture damier volumique
T Damier(in vec3 p, float cellSize)
{
    // Calculez l'indice de cellule en fonction de la position
    int cellIndex = int(floor(p.x / cellSize) + floor(p.y / cellSize) + floor(p.z / cellSize)) % 2;

    // Définissez les couleurs des cases
    vec3 color1 = vec3(1., 1., 1.);
    vec3 color2 = vec3(0., 0., 0.);
    vec3 color = mix(color1, color2, float(cellIndex));

    float specular = 0.25;
    return T(color, specular);
}

// Texture de marbre avec veines
vec3 MarbreVeines(vec3 p)
{
    // Paramètres de la texture du marbre
    int octaves = 5;
    float lacunarity = 3.0;
    float gain = 0.75;
    float scale = 1.;

    // Définissez la couleur de base du marbre
    vec3 baseColor = vec3(0.7, 0.7, 0.7); // Gris clair
    float marbleTurbulence = Turbulence(p * scale, octaves, lacunarity, gain);
    vec3 marbleColor = baseColor + 0.15 * marbleTurbulence; // Ajustez le facteur de turbulence selon vos préférences

    return marbleColor;
}

// Texture de marbre sans veines
vec3 MarbrePoli(vec3 p)
{
    // Paramètres de la texture du marbre
    int octaves = 5;
    float lacunarity = 20.0;
    float gain = 0.75;
    float scale = 1.;

    // Définissez la couleur de base du marbre
    vec3 baseColor = vec3(0.7, 0.7, 0.7); // Gris clair
    float marbleFBM = MouvementBrownien(p * scale, octaves, lacunarity, gain);
    vec3 marbleColor = baseColor + 0.15 * marbleFBM; // Ajustez le facteur de FBM selon vos préférences

    return marbleColor;
}

// Texture de tranche de tronc d'arbre avec des noeuds 
vec3 BoisNoeud(vec3 p)
{
    // Paramètres de la texture de tranche de tronc d'arbre
    float ringsScale = 20.0; 
    float ringsNoiseScale = 2.0; 
    float ringsSharpness = 10.0; 
    float knotsScale = 15.0; 
    float knotsIntensity = 0.5; 

    // Calcul des coordonnées polaires pour simuler les anneaux de croissance
    float r = length(p.xy) * ringsScale;
    float angle = atan(p.y, p.x);
    float rings = cos(r - angle * ringsSharpness);

    // Ajouter du bruit pour les variations dans les anneaux
    float ringsNoise = Noise(p * ringsNoiseScale);
    rings += 0.6 * ringsNoise; // Ajustez l'intensité du bruit selon vos préférences

    // Ajouter des nœuds réalistes
    float knotsNoise1 = Noise(p * knotsScale * 0.5); 
    float knotsNoise2 = Noise(p * knotsScale * 2.0);
    float knots = max(knotsNoise1, knotsNoise2); 
    knots = smoothstep(0.3, 0.8, knots);
    rings *= mix(1.0, knotsIntensity, knots);

    // Ajuster les couleurs pour simuler les différentes couches de croissance
    vec3 baseColor = mix(vec3(0.6, 0.3, 0.1), vec3(0.5, 0.2, 0.1), rings);

    return baseColor;
}

// Compute texture 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= object(p);
    if (vp.i==0){

        return Texture0(p);
    }
    else if (vp.i==1)
    {
        return Texture1(p);
    }
    else if (vp.i==2)
    {
        return Texture2(p);
    }
    else if (vp.i==3)
    {
        return Texture3(p);
    }
    else if (vp.i==4)  // pour une texture uniforme
    {
        return UniformTexture(p);
    }
    else if (vp.i==5)
    {
        float cellSize = 2.0; // Taille du côté du damier (modifiable selon les besoins)
        return Damier(p, cellSize);
    }
    else if (vp.i==6)
    {
        return T(MarbreVeines(p), 0.25);
    }
    else if (vp.i==7)
    {
        return T(MarbrePoli(p), 0.25);
    }
    else if (vp.i==8)
    {
        return T(BoisNoeud(p),0.25);
    }
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p,l,100.0,h,s);
    if (!h)
    {
     return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    T tex=Color(p,n);

    // Ambient color
    vec3 ambient = 0.5*tex.c;

    // Shadow computation
    float s = Shadow(p+0.01*n,n,l);

// Phong diffuse
    vec3 diffuse = 0.5  * clamp(dot(n, l),0.0,1.0) * tex.c;

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = tex.s * pow(clamp(dot(r,l),0.0,1.0),28.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + s * (diffuse + specular);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}
