#define NUM_STRIPES 10.0

#define DIRECTION_HORIZONTAL 0
#define DIRECTION_VERTICAL 1
#define DIRECTION DIRECTION_HORIZONTAL

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 tuv = uv;
    float stripeSize = 1.0 / NUM_STRIPES;
    float progress = 0.5 * sin(iTime) + 0.5;
    if(DIRECTION == DIRECTION_HORIZONTAL) {
        float indexY = floor((1.0 - tuv.y) / stripeSize) + 1.0;
        bool isEven = (int(indexY) & 1) == 1;
        tuv.x += progress * indexY * (isEven ? 1.0 : -1.0);
    } else {
        float indexX = floor((1.0 - tuv.x) / stripeSize) + 1.0;
        bool isEven = (int(indexX) & 1) == 1;
        tuv.y += progress * indexX * (isEven ? 1.0 : -1.0);
    }
    fragColor = (tuv.x > 1.0 || tuv.x < 0.0 || tuv.y < 0.0 || tuv.y > 1.0) 
        ? texture(iChannel1, uv) 
        : texture(iChannel0, tuv);
}
