// more readable version below


void mainImage( out vec4 O, vec2 u ) {
    vec2  R =  iResolution.xy,
          U = ( u+u - R ) / R.y;                         // normalized coordinates
    float a = atan(U.y,U.x), l = length(U), b;           // polar coordinates
    U = l * cos( mod(a,.785 ) - .393 + vec2(0,11));      // 8-rotational symmetry
    U.y = abs(U.y);// ↑ mod 2pi/8                        // in-octant bi-symmetry
    O = 1.- vec4( abs(U.x-.535) < .2                     // bars area in octant
                   ? b = sin(50.*U.x), 
                     clamp(  min( b/fwidth(b),           // draw bars
                                 (abs(.92*U.y-.38*U.x)-.05)*R.y ), 0.,1. ) // octant spacing
                     - float(  (0x960f3c >> 8*int( 7.5*l-2.5 )  // level id  3.*(U.x-.535+.2)/.4
                                            + int( 1.27*a+4. )  // octant id 4* ( a/pi+1 ) 
                               ) %2)  // is hole ?
                       * max(0.,R.y/2. *(.04-U.y))       // draw hole
                   : 0.                                  // background
                );
}





