const float TAU = 6.283185307179586;
const float FREQ = 120.; // fundamental frequency, in hertz
const float LOOP_TIME = 12.;
const float N_HARMONICS = 50.;
const float AMP = 1.;

vec2 sound(float time) {
    float s = mod(time, LOOP_TIME) / LOOP_TIME;
    float cutoff = 1.5 + 2.*N_HARMONICS * s*(0.25 + 0.75*s*s);
    float wave = 0.;
    for (float k = 1.; k < cutoff; k += 2.) {
        wave += sin(TAU * FREQ * k * time) / k;
    }
    return vec2(8./TAU * AMP * wave);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 u = vec2(1./FREQ, 2.) * (2.*fragCoord - vec2(0., iResolution.y)) / iResolution.xy;

    vec3 color;
    if (u.y < sound(ceil(FREQ*iTime)/FREQ + u.x).x) {
        color = vec3(0.9, 0.8, 1.0);
    } else {
        color = vec3(0.3, 0.2, 0.4);
    }

    fragColor = vec4(color, 1.);
}

