void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2  m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y;


    float r = length(uv);
    float theta = atan(uv.y, uv.x);


    float rot = iTime * 0.04 + r * r * r * 0.2;
    float zoom = iTime;


    float space = 3.1415926535 / 16.;
    float phase = mod(theta + rot, space) / space * 2. - 1.; // -1 to 1

    float rep = 0.03 * r + 0.4;

    float ringNom = r + cos(phase) * 0.2 + 1.;
    float val = mod(ringNom, rep) / rep; // 0 to 1
    float offset = abs(fract(val - zoom) - 0.5) * 2.;

    float ring1 = floor(abs(fract(ringNom / rep - zoom) - 0.5) * 2. + 0.05);
    float ring2 = floor(abs(fract(ringNom / rep - zoom + 0.6) - 0.5) * 2. + 0.05);

    vec3 col = vec3(offset, ring1, ring2);


    fragColor = vec4(col,1.0);
}
