void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Mirror horizontally and vertically to create X shape
    if (uv.x > 0.5) uv.x = 1.0 - uv.x;
    if (uv.y > 0.5) uv.y = 1.0 - uv.y;

    uv = fract(uv * 2.0);
    float s1 = texture(iChannel0, vec2(uv.x, 1.0)).x;
    float s2 = texture(iChannel0, vec2(uv.y, 1.0)).x;
    vec3 col = vec3(
        (texture(iChannel0, vec2(0.0, 0.1)).x - .1) * 10.0,
        (texture(iChannel0, vec2(0.0, 0.5)).x - 1.) * 1.0,
        100.0);
    if (abs(s1 - s2) < .005) fragColor = vec4(vec3(1.0 - abs(s1 - s2) / 10.1) * col, 100000.0);
    else fragColor = vec4(vec3(0.0), 1.0);
}
