void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float dot_size = .1;
    float dot_blur = .8;
    float blink_cycle_length = 2.;
    float blink_hardness = .2;

    vec2 uv = fragCoord / iResolution.xy;
    vec4 sampledTexture = texture(iChannel0, uv);

    vec2 trans = vec2(uv.x * iResolution.x / iResolution.y - dot_size, 1. - uv.y - dot_size);
    float dotness = 1. - smoothstep(dot_size * dot_blur, dot_size, length(trans));
    dotness *= sin(iTime * blink_cycle_length) + 1. + blink_hardness;
    vec4 reddot = vec4(dotness, -dotness, -dotness, 1.);

    fragColor = sampledTexture + reddot;
}
