void mainImage( out vec4 O, in vec2 fc )
{
    vec2 uv = (2.0 * fc.xy - iResolution.xy)/iResolution.y;
    uv = fract(uv * 2.0);
    uv -= vec2(0.5);

    float cir = smoothstep(0.4,0.41,length(uv));

    vec3 color = vec3(cir);

    vec3 bgcolor = vec3(0.9,0.1,0.4);
    color = color * bgcolor;

    O = vec4(color,1.0);
}
