// More point containment queries here: https://www.shadertoy.com/playlist/fXdfzX

#if HW_PERFORMANCE==0
// lots of error, but plays well on mobile
#define NEWTON_ITERATIONS 5
#define NUM_STEPS 8
#else
#define NEWTON_ITERATIONS 5
#define NUM_STEPS 32    
#endif

#define CONVERGED_ERROR 1e-3
#define DIVERGED_ERROR 1e6

#define EXPOSURE 2.5


void quadraticPyramidInterpolationFunctions(vec3 rst,
    out float w0, out float w1, out float w2, out float w3, out float w4,
    out float w5, out float w6, out float w7, out float w8,
    out float w9, out float w10, out float w11, out float w12)
{
    // Parameteric (0,1) -> isoparametric (-1,1)
    float r = 2.0 * (rst[0] - 0.5);
    float s = 2.0 * (rst[1] - 0.5);
    float t = 2.0 * (rst[2] - 0.5);
    float r2 = r * r;
    float s2 = s * s;
    float t2 = t * t;

    w0 = -(1. - r) * (1. - s) * (1. - t) * (4. + 3. * r + 3. * s + 2. * r * s + 2. * t + r * t + s * t + 2. * r * s * t) / 16.0;
    w1 = -(1. + r) * (1. - s) * (1. - t) * (4. - 3. * r + 3. * s - 2. * r * s + 2. * t - r * t + s * t - 2. * r * s * t) / 16.0;
    w2 = -(1. + r) * (1. + s) * (1. - t) * (4. - 3. * r - 3. * s + 2. * r * s + 2. * t - r * t - s * t + 2. * r * s * t) / 16.0;
    w3 = -(1. - r) * (1. + s) * (1. - t) * (4. + 3. * r - 3. * s - 2. * r * s + 2. * t + r * t - s * t - 2. * r * s * t) / 16.0;

    w4 = t * (1. + t) / 2.0;

    w5 = (1. - r2) * (1. - s ) * (1. - t) * (2. + s + s * t) / 8.0;
    w6 = (1. + r ) * (1. - s2) * (1. - t) * (2. - r - r * t) / 8.0;
    w7 = (1. - r2) * (1. + s ) * (1. - t) * (2. - s - s * t) / 8.0;
    w8 = (1. - r ) * (1. - s2) * (1. - t) * (2. + r + r * t) / 8.0;

    w9  = (1. - r) * (1. - s) * (1. - t2) / 4.0;
    w10 = (1. + r) * (1. - s) * (1. - t2) / 4.0;
    w11 = (1. + r) * (1. + s) * (1. - t2) / 4.0;
    w12 = (1. - r) * (1. + s) * (1. - t2) / 4.0;
}

void quadraticPyramidInterpolationDerivs(vec3 rst,
    out vec3 dw0, out vec3 dw1, out vec3 dw2, out vec3 dw3, out vec3 dw4,
    out vec3 dw5, out vec3 dw6, out vec3 dw7, out vec3 dw8,
    out vec3 dw9, out vec3 dw10, out vec3 dw11, out vec3 dw12)
{
    // Parameteric (0,1) -> isoparametric (-1,1)
    float r = 2. * rst.x - 1.;
    float s = 2. * rst.y - 1.;
    float t = 2. * rst.z - 1.;

    float rm = 1.0 - r;
    float rp = 1.0 + r;
    float sm = 1.0 - s;
    float sp = 1.0 + s;
    float tm = 1.0 - t;
    float r2 = 1.0 - r * r;
    float t2 = 1.0 - t * t;

    // Multiply by 2.0 to account for the scaling of the derivatives
    dw0.x = -0.125 * (sm * tm - 2.0 * r * sm * tm - s * sm * tm - t * sm * tm - 2.0 * sm * tm);
    dw0.y  = -0.125 * (rm * tm - 2.0 * s * rm * tm - r * rm * tm - t * rm * tm - 2.0 * rm * tm);
    dw0.z = -0.125 * (rm * sm - 2.0 * t * rm * sm - r * rm * sm - s * rm * sm - 2.0 * rm * sm);
    dw0 *= 2.0;

    dw1.x = 0.125 * (sm * tm + 2.0 * r * sm * tm - s * sm * tm - t * sm * tm - 2.0 * sm * tm);
    dw1.y  = -0.125 * (rp * tm - 2.0 * s * rp * tm + r * rp * tm - t * rp * tm - 2.0 * rp * tm);
    dw1.z = -0.125 * (rp * sm - 2.0 * t * rp * sm + r * rp * sm - s * rp * sm - 2.0 * rp * sm);
    dw1 *= 2.0;

    dw2.x = 0.125 * (sp * tm + 2.0 * r * sp * tm + s * sp * tm - t * sp * tm - 2.0 * sp * tm);
    dw2.y  = 0.125 * (rp * tm + 2.0 * s * rp * tm + r * rp * tm - t * rp * tm - 2.0 * rp * tm);
    dw2.z = -0.125 * (rp * sp - 2.0 * t * rp * sp + r * rp * sp + s * rp * sp - 2.0 * rp * sp);
    dw2 *= 2.0;

    dw3.x = -0.125 * (sp * tm - 2.0 * r * sp * tm + s * sp * tm - t * sp * tm - 2.0 * sp * tm);
    dw3.y  = 0.125 * (rm * tm + 2.0 * s * rm * tm - r * rm * tm - t * rm * tm - 2.0 * rm * tm);
    dw3.z = -0.125 * (rm * sp - 2.0 * t * rm * sp - r * rm * sp + s * rm * sp - 2.0 * rm * sp);
    dw3 *= 2.0;

    dw4.x = 0.0;
    dw4.y = 0.0;
    dw4.z = 0.5 + t;
    dw4 *= 2.0;

    dw5.x = -0.5 * r * sm * tm;
    dw5.y  = -0.25 * tm * r2;
    dw5.z = -0.25 * (sm - r * r * sm);
    dw5 *= 2.0;

    dw6.x = 0.25 * (tm - s * s * tm);
    dw6.y  = -0.5 * tm * s * rp;
    dw6.z = -0.25 * (rp - s * s * rp);
    dw6 *= 2.0;

    dw7.x = -0.5 * r * sp * tm;
    dw7.y  = 0.25 * tm * r2;
    dw7.z = -0.25 * (sp - r * r * sp);
    dw7 *= 2.0;

    dw8.x = -0.25 * (tm - s * s * tm);
    dw8.y  = -0.5 * tm * s * rm;
    dw8.z = -0.25 * (rm - s * s * rm);
    dw8 *= 2.0;

    dw9.x  = -0.25 * (1. - s) * (1. - t * t);
    dw9.y  = -0.25 * rm * t2;
    dw9.z  = -0.5 * (1. - r) * (1. - s) * t;
    dw9 *= 2.0;

    dw10.x = 0.25 * (1. - s) * (1. - t * t);
    dw10.y = -0.25 * rp * t2;
    dw10.z = -0.5 * (1. + r) * (1. - s) * t;
    dw10 *= 2.0;

    dw11.x = 0.25 * (1. + s) * (1. - t * t);
    dw11.y = 0.25 * rp * t2;
    dw11.z = -0.5 * (1. + r) * (1. + s) * t;
    dw11 *= 2.0;

    dw12.x = -0.25 * (1. + s) * (1. - t * t);
    dw12.y = 0.25 * rm * t2;
    dw12.z = -0.5 * (1. - r) * (1. + s) * t;
    dw12 *= 2.0;
}


bool pointInCannonicalPyramid( in vec3 rst)
{
    vec3 p = vec3(rst.x - 0.5, rst.z, rst.y - 0.5);

    // symmetry
    p.xz = abs(p.xz); // do p=abs(p) instead for double pyramid
    p.xz = (p.z>p.x) ? p.zx : p.xz;
    p.xz -= 0.5;

    // project into face plane (2D)
    float qz = p.x + 0.5 * p.y;
    float si = sign(max(qz, -p.y));
    return si < 0.0;
}

bool interpolateQuadraticPyramid(vec3 P, 
    inout vec4 V0, inout vec4 V1, inout vec4 V2, inout vec4 V3, inout vec4 V4, inout vec4 V5, 
    inout vec4 V6, inout vec4 V7, inout vec4 V8, inout vec4 V9, inout vec4 V10, inout vec4 V11, inout vec4 V12) {

    // Initialize our canonical coordinates to be in the center of the element
    // We'll iteratively refine these with Newton's method to match the given "P"
    vec3 pc_curr = vec3(0.5, 0.5, 0.2);
    vec3 pc = vec3(0.5, 0.5, 0.2);

    // Iteration for Newton's method
    bool converged = false;
    for (int iteration = 0; iteration < NEWTON_ITERATIONS; iteration++) {
        // Compute the currently optimized position in the canonical element
        float w0, w1, w2, w3, w4, w5, w6, w7, w8, w9, w10, w11, w12;
        quadraticPyramidInterpolationFunctions(pc_curr, w0, w1, w2, w3, w4, w5, w6, w7, w8, w9, w10, w11, w12);
        vec3 fcol = V0.xyz * w0 + V1.xyz * w1 + V2.xyz * w2 + V3.xyz * w3 + V4.xyz * w4 + V5.xyz * w5 
                  + V6.xyz * w6 + V7.xyz * w7 + V8.xyz * w8 + V9.xyz * w9 + V10.xyz * w10 + V11.xyz * w11 + V12.xyz * w12;
        fcol -= P; // Making P be the origin

        // Now compute Jacobian, which transforms change in pc to change in shape functions
        vec3 dw0, dw1, dw2, dw3, dw4, dw5, dw6, dw7, dw8, dw9, dw10, dw11, dw12;
        quadraticPyramidInterpolationDerivs(pc_curr, dw0, dw1, dw2, dw3, dw4, dw5, dw6, dw7, dw8, dw9, dw10, dw11, dw12);

        // Now get derivatives in world space
        vec3 rcol = V0.xyz * dw0.x + V1.xyz * dw1.x + V2.xyz * dw2.x + V3.xyz * dw3.x + V4.xyz * dw4.x + V5.xyz * dw5.x 
                  + V6.xyz * dw6.x + V7.xyz * dw7.x + V8.xyz * dw8.x + V9.xyz * dw9.x + V10.xyz * dw10.x + V11.xyz * dw11.x + V12.xyz * dw12.x;
        vec3 scol = V0.xyz * dw0.y + V1.xyz * dw1.y + V2.xyz * dw2.y + V3.xyz * dw3.y + V4.xyz * dw4.y + V5.xyz * dw5.y 
                  + V6.xyz * dw6.y + V7.xyz * dw7.y + V8.xyz * dw8.y + V9.xyz * dw9.y + V10.xyz * dw10.y + V11.xyz * dw11.y + V12.xyz * dw12.y;
        vec3 tcol = V0.xyz * dw0.z + V1.xyz * dw1.z + V2.xyz * dw2.z + V3.xyz * dw3.z + V4.xyz * dw4.z + V5.xyz * dw5.z 
                  + V6.xyz * dw6.z + V7.xyz * dw7.z + V8.xyz * dw8.z + V9.xyz * dw9.z + V10.xyz * dw10.z + V11.xyz * dw11.z + V12.xyz * dw12.z;

        // The above should be our 3X3 jacobian.

        // Compute determinants and generate improvements
        float d = determinant(mat3(rcol, scol, tcol));
        if (abs(d) < 1e-20) {
            return false; // Determinant too small, likely singular
        }
        // Newton Raphson update formula, x_new = x_old - f(x) / f'(x), but generalized to 
        // multiple dimensions. fcol has principle values, while other two contain gradients, 
        // so the ratio effectively calculates changes needed using Cramer's rule.
        pc.x = pc_curr.x - (determinant(mat3(fcol, scol, tcol)) / d);
        pc.y = pc_curr.y - (determinant(mat3(rcol, fcol, tcol)) / d);
        pc.z = pc_curr.z - (determinant(mat3(rcol, scol, fcol)) / d);

        vec3 err = abs(pc - pc_curr);
        pc_curr = pc; // Commit the updated canonical coordinates

        // Check for convergence
        if (all(lessThan(err, vec3(CONVERGED_ERROR)))) {
            converged = true;
            break;
        }
    }

    if (!converged) return false;
    if (!pointInCannonicalPyramid(pc_curr)) return false;

    quadraticPyramidInterpolationFunctions(pc_curr, V0.w, V1.w, V2.w, V3.w, V4.w, V5.w, V6.w, V7.w, V8.w, V9.w, V10.w, V11.w, V12.w);   

    return true;
}

//-----------------------------------------------------------------------------
// Utils
//-----------------------------------------------------------------------------

vec3 viridis(float t) {
    t = clamp(t, 0.0, 1.0);

    const vec3 c0 = vec3(0.2777273272234177, 0.005407344544966578, 0.3340998053353061);
    const vec3 c1 = vec3(0.1050930431085774, 1.404613529898575, 1.384590162594685);
    const vec3 c2 = vec3(-0.3308618287255563, 0.214847559468213, 0.09509516302823659);
    const vec3 c3 = vec3(-4.634230498983486, -5.799100973351585, -19.33244095627987);
    const vec3 c4 = vec3(6.228269936347081, 14.17993336680509, 56.69055260068105);
    const vec3 c5 = vec3(4.776384997670288, -13.74514537774601, -65.35303263337234);
    const vec3 c6 = vec3(-5.435455855934631, 4.645852612178535, 26.3124352495832);

    vec3 srgb = c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
    return pow(srgb, vec3(2.2));

}

vec4 over(vec4 a, vec4 b) {
  vec4 result;
  result.a = a.a + b.a * (1.f - a.a);
  if (result.a == 0.f)
    return a; // avoid NaN
  result.rgb = (a.rgb * a.a + b.rgb * b.a * (1.f - a.a)) / result.a;
  return result;
}

#define M_2PI 6.2831853071795865f
#define M_GRC 0.61803398875f

//-----------------------------------------------------------------------------
// Main functions
//-----------------------------------------------------------------------------
vec4 query(vec3 p)
{	
    float time = iTime*4.f;

    // Corner vertices
    vec4 v00 = vec4(cos((1.0 / 4.0) * M_2PI), -.75, sin((1.0 / 4.0) * M_2PI), 0.0);
    vec4 v01 = vec4(cos((2.0 / 4.0) * M_2PI), -.75, sin((2.0 / 4.0) * M_2PI), 0.0);
    vec4 v02 = vec4(cos((3.0 / 4.0) * M_2PI), -.75, sin((3.0 / 4.0) * M_2PI), 0.0);
    vec4 v03 = vec4(cos((4.0 / 4.0) * M_2PI), -.75, sin((4.0 / 4.0) * M_2PI), 0.0);
    vec4 v04 = vec4( 0.0, .75, 0.0, 0.0);

    vec4 cen = (v00 + v01 + v02 + v03 + v04) / 5.0;

    // Edge vertices
    vec4 v05 = vec4((v00 + v01).xyz * .5f, 0.0); // -> midpoint between 0 and 1
    vec4 v06 = vec4((v01 + v02).xyz * .5f, 0.0); // -> midpoint between 1 and 2
    vec4 v07 = vec4((v02 + v03).xyz * .5f, 0.0); // -> midpoint between 2 and 3
    vec4 v08 = vec4((v03 + v00).xyz * .5f, 0.0); // -> midpoint between 3 and 0
    vec4 v09 = vec4((v00 + v04).xyz * .5f, 0.0); // -> midpoint between 0 and 4
    vec4 v10 = vec4((v01 + v04).xyz * .5f, 0.0); // -> midpoint between 1 and 4
    vec4 v11 = vec4((v02 + v04).xyz * .5f, 0.0); // -> midpoint between 2 and 4
    vec4 v12 = vec4((v03 + v04).xyz * .5f, 0.0); // -> midpoint between 3 and 4

    // Make the edge vertices wiggle
    v05 = v05 + .2 * normalize(v05 - cen) * cos(time * .1);
    v06 = v06 + .2 * normalize(v06 - cen) * cos(time * .2);
    v07 = v07 + .2 * normalize(v07 - cen) * cos(time * .3);
    v08 = v08 + .2 * normalize(v08 - cen) * cos(time * .4);
    v09 = v09 + .2 * normalize(v09 - cen) * cos(time * .5);
    v10 = v10 + .2 * normalize(v10 - cen) * cos(time * .6);
    v11 = v11 + .2 * normalize(v11 - cen) * cos(time * .7);
    v12 = v12 + .2 * normalize(v12 - cen) * cos(time * .8);

    if (!interpolateQuadraticPyramid(p, v00, v01, v02, v03, v04, v05, v06, v07, v08, v09, v10, v11, v12)) return vec4(0.0);

    // data values weighted by barycentrics
    float val00 = (.5 + .5 * cos(time*0.1)) * v00.w;
    float val01 = (.5 + .5 * cos(time*0.2)) * v01.w;
    float val02 = (.5 + .5 * cos(time*0.3)) * v02.w;
    float val03 = (.5 + .5 * cos(time*0.4)) * v03.w;
    float val04 = (.5 + .5 * cos(time*0.5)) * v04.w;
    float val05 = (.5 + .5 * cos(time*0.6)) * v05.w;
    float val06 = (.5 + .5 * cos(time*0.7)) * v06.w;
    float val07 = (.5 + .5 * cos(time*0.8)) * v07.w;
    float val08 = (.5 + .5 * cos(time*0.9)) * v08.w;
    float val09 = (.5 + .5 * cos(time*1.0)) * v09.w;
    float val10 = (.5 + .5 * cos(time*1.1)) * v10.w;
    float val11 = (.5 + .5 * cos(time*1.2)) * v11.w;
    float val12 = (.5 + .5 * cos(time*1.3)) * v12.w;
    float val = val00 + val01 + val02 + val03 + val04 
              + val05 + val06 + val07 + val08 + val09 + val10 + val11 + val12;

    // Colormapped value and a hardcoded optical density
    return vec4(viridis(val) * EXPOSURE, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 v = -1.0 + 2.0*q;
    v.x *= iResolution.x/ iResolution.y;

    float an = 0.25*iTime + 6.283185*iMouse.x/iResolution.x;
    vec3 ro = vec3( 3.5*cos(an), 0.7, 3.5*sin(an) );
    vec3 ta = vec3( 0.0, -0.1, 0.0 );

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    // create view ray
    vec3 rd = normalize( v.x*uu + v.y*vv + 3.0*ww );

	vec4 color = vec4(.0);

    float startRayOffset = 0.0f;
    int frame = iFrame % 64;

    // blue noise
    startRayOffset = texture(iChannel0, fragCoord / 1024.0f).r;
    startRayOffset = fract(startRayOffset + float(frame) * M_GRC);


	float zMin         = 2.0;
    float zMax         = 5.0;
	float step         = (zMax - zMin) / float(NUM_STEPS);
    vec3 p             = ro + rd * zMin + rd * step * startRayOffset;    
	for(int i=0; i<NUM_STEPS; i++)
	{
		vec4 rgbd = query(p);
        float density = rgbd[3];
        float rho = 1.0 - exp(-density * step);
        color = over(color, vec4(rgbd.xyz, rho));
        if (color.a > .95) break;
		p += rd*step;
	}

    // Composite over a background
    color = over(color, vec4(vec3(0.01)*(1.0-0.2*length(v)), 1.0));

    color.rgb = pow(color.rgb, vec3(1.0/2.2));

    // cheap dithering
    color.rgb += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    fragColor = color;

}
