void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=fragCoord.xy/iResolution.xy*6.;
    for(float i;i<2.;i++){
        //vec4 o=sin((p.xyxy*.3+vec4(0,1,2,3))*.5+.5);
        p.x+=sin(p.y+iTime*.3);
        p*=mat2(4,-8,8,4)/8.;
    }

    // Output to screen
    //fragColor=sin(p.xxxx*.3+vec4(0,1,2,3))*.5+.5;
    float c = 1.0 - smoothstep(0.0,1.1,fract(p.x));
    //float c = 1.0 - smoothstep(0.0,0.5*(sin(0.5*p.y)+2.),fract(p.x));
    //float c = 1.0 - smoothstep(0.0,1.2,fract(p.y));
    fragColor = vec4(vec3(c), 1.0);
    //fragColor=vec4(p.xyy/50.,1.0);
}
