void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 R = iResolution.xy,
         u = abs(fragCoord + fragCoord - R) / R.y * 0.7; 
    float l = length(u), 
          t = iTime, ct = cos(t),
          i;

    u *= mat2(cos(t / 4. + vec4(0, 33, 11, 0)));

    fragColor = vec4(0);

    for (float i = 0.; i < 1.; i++) {
        float res = .18 / abs(
                        sin(
                            length(u = fract(u * (1.35 + ct * .2)) - .5) 
                            * exp(length(u) - l) * 8. - t
                        ));
        fragColor += res * res;
    }

    fragColor *= (1. + cos(l * (6. - 0.0 * 3.) + t * 4. + vec4(0, 2, 3, 0))) * smoothstep(0., .3, t); 
}
