float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    uv.y *= 0.8;
    float l = length(uv);

    vec3 color = vec3(1, 3, 5);
    vec2 wv = uv * 0.1;
    float d = length(wv);
    float w = 0.022 / d;
    w = w * w * w;
    color *= w;

    if (l > 0.3) {
        fragColor = vec4(color, 1.0);
    }
    else {
        uv *= 0.01;
        float speed = 0.7;
        float size = 100.0;
        float a = noise(vec2(uv.x*size*5.0+3.0*speed*iTime, uv.y*size*3.0+3.0*speed*iTime));
        float b = noise(vec2(uv.x*size*8.0+2.0*speed*iTime, uv.y*size*8.0-2.0*speed*iTime));
        float c = noise(vec2(uv.x*size*6.0-2.5*speed*iTime, uv.y*size*6.0+1.0*speed*iTime));
        float d = noise(vec2(uv.x*size*4.0-1.0*speed*iTime, uv.y*size*5.0-2.0*speed*iTime));
        a = 0.1 / a;
        b = 0.1 / b;
        c = 0.1 / c;
        d = 0.1 / d;
        vec3 color1 = vec3(1, 3, 5);
        vec3 color2 = vec3(1, 5, 3);
        vec3 color3 = vec3(3, 1, 5);
        vec3 color4 = vec3(1, 1, 3);
        fragColor = vec4((a*color1+b*color2+c*color3+d*color4)/7.0, 1.0);
        //fragColor = vec4(c*color1, 1.0);
    }
}
