void mainImage(out vec4 fragColor, in vec2 fragCoord) { 
    vec2 noise_uv = fragCoord/iResolution.xy;
    noise_uv.x += iTime/5.;
    vec3 noiseVal = texture(iChannel0,noise_uv).rgb;

    vec2 uv = fragCoord/iResolution.xy;
    float noiseWeight = 0.04;
    uv.x += noiseVal.b * noiseWeight;
    uv.y += noiseVal.r * noiseWeight;
    vec3 col = texture(iChannel0,uv).rgb;

    fragColor.rgb = vec3(col);

}
