void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float r = radians(45.);//角度转弧度
    float g = cos(r) * uv.x   - sin(r) * uv.y- iTime* 0.3;//旋转45度

    float m = step(mod(g * 5.,1.) ,0.5);//生成条纹

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));    
    col = mix(col,vec3(1.)-col,m);

    col *= 1. - smoothstep(0.,0.07,abs(0.5-uv.x));//横向长度
    col *= 1. - smoothstep(0.3,0.32,abs(0.5-uv.y));//纵向长度

    if(abs(0.5-uv.y)>0.3 &&abs(0.5-uv.y) < 0.35)//一些无关紧要的装饰
        col += (smoothstep(.08, .03, abs(uv.x - .5)));
    if(abs(0.5-uv.y)>0.35 &&abs(0.5-uv.y) < 0.42)
        col += (smoothstep(.07, .02, abs(uv.x - .5)));

    fragColor = vec4(col,1.0);
}
