void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float angle = iTime + 0.05;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 normalizedCord = fragCoord / iResolution.xy * 2.0 - 1.0;
    // fragColor = vec4(length(normalizedCord), length(normalizedCord) , length(normalizedCord), 1.0);


    for(float i = 0.0; i <32.0; i+= 1.0){
        normalizedCord = abs(normalizedCord);
        normalizedCord -= 0.5;
        normalizedCord *= 1.1;
        normalizedCord *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }

    fragColor = vec4(length(normalizedCord),
    length(normalizedCord + vec2(0.2, -0.3)),
    length(normalizedCord + vec2(-0.4, -0.1)*iTime),
    1.0);
}
