void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 uv2 = vec3(uv.x, uv.y, uv.x/uv.y);
    vec3 col = 0.6 + 0.5*cos(10.0*uv2 + vec3(0,2.6,4) + iTime);
    for(int i = 0; i < 2; i++)
    {
        col *= cos(col.x) + sin(col.x) + log2(col.x * col.y);

    }

    col *= (1.0 - 10.0*smoothstep(uv2.x, uv2.y, uv2.z)) 
            * ( 1.0 - smoothstep( 0.0, 0.9, uv2.x));



    // Output to screen
    fragColor = vec4(col,1.0);
}
