float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float a = abs(sin(iTime));// 0~1

    float d =sdCircle(uv, .3);

    d = smoothstep(0., a, d);

    vec3 col = vec3(d,d,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
