void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = fragCoord.xy / iResolution.xy;

    float pi = 3.14159265358979323846264;  // Correct value of pi

    float lineWidth = sin(tan(iTime)+0.2)*0.5+0.5;

    float v1 = cos(pos.x+iTime)*0.5+0.5;
    float fv1 = 0.0;
    if ((pos.y > v1) && (pos.y < v1 + lineWidth*0.2)) {
        fv1 = 1.0;
    }

    float v2 = mod(pos.x+iTime/2.,0.124);
    float fv2 = 0.0;
    if ((pos.y > v2) && (pos.y < v2 + lineWidth)) {
        fv2 = 1.0;
    }

    float v3 = fract(pos.x+iTime*2.);  
    float fv3 = 0.0;
    if ((pos.y > v3) && (pos.y < v3 + lineWidth)) {
        fv3 = 1.0;
    }

    vec3 color = vec3(fv1,fv2,fv3);

    // Set fragment color
    fragColor = vec4(color, 1.0);
}
