void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizar las coordenadas
    vec2 uv = fragCoord / iResolution.xy;

    // Tiempo en segundos
    float time = iTime;

    // Duración de cada parpadeo y color sólido
    float strobeDuration = 0.1;  // Duración de cada parpadeo
    float solidDuration = 1.0;   // Duración del color sólido
    float strobeCycles = 3.0;    // Número de parpadeos antes del color sólido

    // Calcular el tiempo total de un ciclo
    float cycleDuration = strobeDuration * strobeCycles + solidDuration;
    float cycleTime = mod(time, cycleDuration);

    // Rosado
    vec3 pink = vec3(1.0, 0.5, 0.75);

    // Fase de estrobo: 3 parpadeos rápidos
    if (cycleTime < strobeDuration * strobeCycles) {
        // Estrobo rápido: alternar entre encendido y apagado rápido
        float strobeEffect = mod(time * 20.0, 2.0);  // 20.0 controla la rapidez
        if (strobeEffect < 1.0) {
            fragColor = vec4(pink, 1.0);  // Color rosado
        } else {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);  // Fondo negro (apagado)
        }
    }
    // Fase de color sólido
    else {
        fragColor = vec4(pink, 1.0);  // Color rosado sólido
    }
}
