float PI = 3.1415;
float atan2(float x, float y) {
    if(x <0.) return y < 0.? atan(y/x) + PI: atan(y/x) - PI;
    else return atan(y/x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float minres = min(iResolution.x, iResolution.y)/2.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5*iResolution.xy) / minres;
    // Normalized r theta coords, x = r, y = theta
    vec2 uvr = vec2(sqrt(uv.x*uv.x + uv.y*uv.y), mod(atan2(uv.y,uv.x),2.*3.14));

    vec3 col = vec3(0.,0.,0.);
    // bottom level colors
    if(uv.y<-0.25) {

        // uvr centered above the x axis
        vec2 uva = (fragCoord - vec2(0.5*iResolution.x, 0.7*iResolution.y)) / minres;
        vec2 uvra = vec2(sqrt(uva.x*uva.x + uva.y*uva.y), mod(atan2(uva.y,uva.x),2.*3.14));

        float y = abs(uva.y + 0.25);

        y/=(y+0.1);

        float mody = mod(uv.y, 0.05);

        if(mod(y - iTime/25., 0.01) < 0.005 || mod(uvra.y, 0.25) < 0.03) {
            col += vec3(0.95+uv.y/1.5,0.25,0.75);
        }
    }
    if(uv.y > -0.25) {

        // abs val x used for waves
        float uvx = abs(uv.x);
        // uvr centered above the x axis
        vec2 uva = (fragCoord - vec2(0.5*iResolution.x, 0.6*iResolution.y)) / minres;
        vec2 uvra = vec2(sqrt(uva.x*uva.x + uva.y*uva.y), mod(atan2(uva.y,uva.x),2.*3.14));

        // sky background
        col = vec3(0.3-uvra.x/8. - uva.y/1., 0, 0.5-uvra.x/4. - uva.y/4.);

        col += vec3(0.1,0.1,0.1);

        // side waves
        if(uv.y < abs(cos(5.*uvx-iTime)+sin(150.*uvx-iTime))*pow(sin(uvx),2.)/2. - 0.25) {
            col = vec3(0.5-uv.y,1,1);
        }

        // sun
        if(uvra.x < 0.35 && (uva.y > 0. || mod(uva.y + iTime/15., 0.075) < 0.075-abs(uva.y)/5.)) {
            col = vec3(1.,0.5+uva.y*1.5,0.) + vec3(1,1,1)*uvra.x;
        }



    }

    // CRT filter?
    float bars = 25.;
    float crtSpeed = 0.05;
    if(mod(uv.y + crtSpeed*iTime,(1./bars)) < (1./bars)/2.) col -= vec3(0.05,0.05,0.05);
    if(mod(uv.y + crtSpeed*2.*iTime,(1./2.)) < (1./2.)/2.) col -= vec3(0.05,0.05,0.05);

    // Output to screen
    fragColor = vec4(col,1.0);
}
