vec3 palette(float t) { // from www.shadertoy.com/view/ddcfzs
    vec3 d = vec3(0.263,0.416,0.557);
    return 0.5+0.5*cos(6.28318*(t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float t = iTime+4.;
    vec2 c = 0.0001*uv+vec2(-0.7648, 0.101);
    vec2 z = c;
    vec3 col = vec3(1);
    for (int i = 0; i < int(clamp(12.*t, 0., 512.))+1; i++) {
        if (dot(z, z) >= 65536.) { // 256 is escape radius, 65536 = 256^2
            float sn = float(i)-log2(log2(dot(z, z)))+5.; col = sn > 12.*t ? vec3(1) : palette(fract(sn/32.)+.25); break;
        }
        z = vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y) + c;
    }
    fragColor = vec4(col, 1);
}
