#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float GetDist(vec3 p) {
	vec4 s = vec4(0, 1, 6, 1);

    float sphereDist =  length(p-s.xyz)-s.w;
    float planeDist = p.y;

    float d = min(sphereDist, planeDist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

// Keep GetLight focused on calculating light intensity
float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n * SURF_DIST * 2., l);
    if (d < length(lightPos - p)) dif *= .1;

    return dif;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // Camera orbit parameters
    float radius = 5.0; // Distance from the center
    float height = 1.0; // Height of the camera from the ground
    float angle = iTime * 0.1; // Controls the speed of the orbit

    // Calculate the camera's position in a circular orbit
    vec3 ro = vec3(cos(angle) * radius, height, sin(angle) * radius);

    // Always looking at the center of the scene (where the sphere is)
    vec3 lookAt = vec3(0.0, 1.0, 0.0); // The sphere's center position
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    // Proceed with ray marching using the adjusted camera position and direction
    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;
    vec3 col;
    float lightIntensity = GetLight(p);

    // Color and light calculations remain the same
    if (d < MAX_DIST) {
        col = (GetDist(p) < 0.1) ?
            vec3(
                0.5 + 0.5 * sin(iTime * 1.5 + length(p) + sin(iTime)),
                0.5 + 0.5 * cos(iTime * 1.7 + length(p) + cos(iTime)),
                0.5 + 0.5 * sin(iTime * 1.3 + length(p))
            ) * lightIntensity :
            vec3(
                0.5 + 0.5 * sin(p.x * 2.0 + iTime * 2.0),
                0.5 + 0.5 * sin(p.y * 2.0 + iTime * 2.0 + 2.0),
                0.5 + 0.5 * sin(p.z * 2.0 + iTime * 2.0 + 4.0)
            ) * lightIntensity;
    } else {
        col = vec3(0.0); // Background color
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));
    fragColor = vec4(col, 1.0);
}
