void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    //Grid settings
    float lineThickness = 2.0; //Thickness of the grid lines
    float lineSpacing = 50.0; //Spacing between the grid lines
    float speed = 50.0; //Speed of diagonal movement

    //Time varying offset for diagonal movement
    float diagonalOffset = iTime * speed;

    //Grid pattern for both axes with diagonal movement
    float gridX = step(mod(fragCoord.x + diagonalOffset, lineSpacing), lineThickness);
    float gridY = step(mod(fragCoord.y + diagonalOffset, lineSpacing), lineThickness);

    //Combined grid patterns
    float gridPattern = max(gridX, gridY);

    //Colors
    vec3 lineColor = vec3(1.0, 0.75, 0.8); // Pastel pink
    vec3 backgroundColor = vec3(1.0); // White

    //Final color
    vec3 col = mix(backgroundColor, lineColor, gridPattern);

    fragColor = vec4(col, 1.0);


}
