#define PI 3.14159265359

float wave(float xPos, float amp, float freq, float speed)
{
    return amp * sin(((2. * PI) * xPos) * freq + iTime * (speed * freq));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv1 = uv;
    uv /= 2. + 0.5;

    uv1.y -= 0.5;

    float sumOfSines = 0.;

    sumOfSines += wave(uv1.x, 0.08, 1.4, 1.);
    sumOfSines += wave(uv1.x, 0.03, 2.5, 1.6);
    sumOfSines += wave(uv1.x, 0.01, 5.2, 2.);
    sumOfSines += wave(uv1.x, 0.006, 8.2, 2.4);

    vec3 col = vec3(0., 0., 0.);
    float sum = 0.;
    if (sumOfSines > uv1.y)
    {
        sum = 1. * uv.y;

        sum = pow(sum, 1.2);

        col += vec3(0.1, 0.4, 1.) * sum * 12.;
        col += 0.05;
    }

    fragColor = vec4(col, 1.0);
}
